                        name: 'filter-demo-modal.component.html',
                        fileContents: "<sky-modal>\n  <sky-modal-header>\n    Food preferences\n  </sky-modal-header>\n  <sky-modal-content>\n    <label for=\"sky-demo-select-type\">Fruit type</label>\n    <select id=\"sky-demo-select-type\" [(ngModel)]=\"fruitType\" class=\"sky-form-control\">\n      <option value=\"any\">Any fruit</option>\n      <option value=\"citrus\">Citrus</option>\n      <option value=\"berry\">Berry</option>\n    </select>\n    <div style=\"margin-top: 15px;\">\n      <sky-checkbox [(ngModel)]=\"hideOrange\">\n        <sky-checkbox-label>\n          Hide orange fruits\n        </sky-checkbox-label>\n      </sky-checkbox>\n    </div>\n  </sky-modal-content>\n  <sky-modal-footer>\n    <button\n      type=\"button\"\n      class=\"sky-btn sky-btn-primary\"\n      (click)=\"applyFilters()\">\n      Apply filters\n    </button>\n    <button\n      type=\"button\"\n      class=\"sky-btn sky-btn-link\"\n      (click)=\"clearAllFilters()\">\n      Clear all filters\n    </button>\n    <button\n      type=\"button\"\n      class=\"sky-btn sky-btn-link\"\n      (click)=\"cancel()\">\n      Cancel\n    </button>\n  </sky-modal-footer>\n</sky-modal>\n"
