                        name: 'repeater-demo.component.html',
                        fileContents: "<sky-repeater\n  [expandMode]=\"expandMode\">\n\n  <sky-repeater-item\n    *ngFor=\"let item of items\"\n    [isExpanded]=\"item.expanded\"\n    [selectable]=\"selectable\">\n\n    <sky-repeater-item-title\n      ng-if=\"repeaterCtrl.showTitle\">\n      <div class=\"pull-left\">\n        {{item.title}}\n      </div>\n      <div class=\"pull-right\">\n        <span\n          class=\"label\"\n          ng-class=\"repeaterCtrl.getStatusCls(item)\">\n          {{item.status}}\n        </span>\n      </div>\n    </sky-repeater-item-title>\n    <sky-repeater-item-content>\n      <div>\n        {{item.note}}\n      </div>\n    </sky-repeater-item-content>\n  </sky-repeater-item>\n</sky-repeater>\n\n<p>\n  <label>\n    Expand mode:\n    <select [(ngModel)]=\"expandMode\">\n      <option value=\"none\">\n        None\n      </option>\n      <option value=\"single\">\n        Single\n      </option>\n      <option value=\"multiple\">\n        Multiple\n      </option>\n    </select>\n  </label>\n  <button class=\"sky-btn\" (click)=\"addItem()\">Add item</button>\n  <button class=\"sky-btn\" (click)=\"removeItem()\">Remove last item</button>\n  <sky-checkbox [(ngModel)]=\"selectable\">\n    <sky-checkbox-label>\n      Show checkbox\n    </sky-checkbox-label>\n  </sky-checkbox>\n</p>\n"
