styles:
.sky-file-drop-col {
  margin-bottom: 10px;
  padding-left: 5px;
  padding-right: 5px;
  position: relative;
}

@media (min-width: 768px) {
  .sky-file-drop-row {
    display: flex;
  }
  .sky-file-drop-col {
    flex-basis: 100%;
  }
  .sky-file-drop-allow-links.sky-file-drop-col {
    flex-basis: 50%;
  }
}

button.sky-file-drop {
  height: 100%;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1;
}

button.sky-file-drop:hover {
  cursor: pointer;
}

.sky-file-drop-contents {
  border-top: 1px solid #cdcfd2;
  border-bottom: 1px solid #cdcfd2;
  border-left: 1px solid #cdcfd2;
  border-right: 1px solid #cdcfd2;
  background-color: #eeeeef;
  padding: 10px;
  color: #282b31;
  cursor: pointer;
  width: 100%;
  text-align: center;
  height: 100%;
}

.sky-file-drop-contents .sky-file-drop-link-header {
  margin-bottom: 5px;
}

.sky-file-drop-noclick .sky-file-drop-contents {
  cursor: default;
}

.sky-file-drop-accept .sky-file-drop-contents,
.sky-file-drop-reject .sky-file-drop-contents {
  border-style: dashed;
  border-width: 1px;
}

.sky-file-drop-accept .sky-file-drop-contents-not-over,
.sky-file-drop-reject .sky-file-drop-contents-not-over {
  display: none;
}

.sky-file-drop-accept .sky-file-drop-contents {
  border-color: #71bf43;
  color: #282b31;
}

.sky-file-drop-accept .sky-file-drop-contents-accept {
  display: block;
}

.sky-file-drop-reject .sky-file-drop-contents {
  border-color: #ef4044;
  color: #282b31;
}

.sky-file-drop-reject .sky-file-drop-contents-reject {
  display: block;
}

.sky-file-drop-contents-accept,
.sky-file-drop-contents-reject {
  display: none;
}

.sky-file-upload-icon {
  display: block;
  font-size: 40px;
  margin-top: 10px;
}

.sky-file-drop-link {
  cursor: default;
}

.sky-file-drop-text-header {
  font-family: "Blackbaud Sans Condensed", "Helvetica Neue Condensed", "Arial Narrow";
  color: #282b31;
  font-weight: 500;
  font-size: 22px;
  margin: 0;
}

.sky-file-drop-text {
  font-size: 15px;
  margin-top: 5px;
  margin-bottom: 20px;
}

.sky-file-drop-text,
.sky-file-drop-text-header {
  line-height: 1.1;
  display: block;
}

.sky-file-upload-icon {
  color: #686c73;
}

.sky-file-drop {
  background-color: transparent;
  border: none;
  display: block;
  -webkit-appearance: none;
  width: 100%;
  padding: 0;
}

.sky-file-input-hidden {
  display: none;
}

