styles:
.sky-modal {
  border-top: 1px solid #cdcfd2;
  border-bottom: 1px solid #cdcfd2;
  border-left: 1px solid #cdcfd2;
  border-right: 1px solid #cdcfd2;
  position: fixed;
  width: auto;
  left: 0;
  right: 0;
  top: 20px;
  margin: 10px;
  display: flex;
  flex-direction: column;
}

.sky-modal:focus {
  outline: none;
}

@media (min-width: 768px) {
  .sky-modal:not(.sky-modal-large) {
    margin: 0 auto;
  }
  .sky-modal-small {
    width: 300px;
  }
  .sky-modal-medium {
    width: 600px;
  }
}

@media (min-width: 920px) {
  .sky-modal-large {
    margin: 0 auto;
    width: 900px;
  }
}

.sky-modal-content {
  background-color: #fff;
  padding: 15px;
}

.sky-modal-tiled .sky-modal-content {
  background-color: #eeeeef;
}

.sky-modal-tiled .sky-modal-content /deep/ .sky-tile-title {
  font-family: "Blackbaud Sans", "Helvetica Neue", Arial, sans-serif;
  color: #686c73;
  font-weight: 300;
  font-size: 19px;
}

.sky-modal-tiled .sky-modal-content /deep/ .sky-tile-tools .sky-chevron {
  margin: 9px 9px 8px 0;
}

.sky-modal-header {
  /* Need less padding because of the close button padding */
  padding-left: 15px;
  padding-top: 9px;
  padding-bottom: 9px;
  padding-right: 3px;
  background-color: #fff;
  display: flex;
  align-items: baseline;
  border-bottom: 1px solid #e2e3e4;
}

.sky-modal-header-buttons .sky-btn {
  border: none;
  color: #cdcfd2;
  cursor: pointer;
}

.sky-modal-header-buttons .sky-btn:hover {
  color: #979ba2;
  transition: color 150ms;
}

.sky-modal-header-content {
  flex-grow: 1;
  font-family: "Blackbaud Sans", "Helvetica Neue", Arial, sans-serif;
  color: #282b31;
  font-weight: 600;
  font-size: 16px;
}

.sky-modal-header {
  flex-shrink: 0;
}

.sky-modal-content {
  overflow-y: auto;
}

.sky-modal-footer {
  flex-shrink: 0;
}

.sky-modal-footer /deep/ sky-tabset-nav-button + sky-tabset-nav-button {
  margin-left: 10px;
}

.sky-modal-footer /deep/ sky-tabset-nav-button + .sky-btn {
  margin-left: 10px;
}

.sky-modal-footer /deep/ .sky-btn + .sky-btn {
  margin-left: 10px;
}

.sky-modal-footer /deep/ .sky-btn + .sky-btn-link {
  margin-left: -2px;
  /* offset the padding of the btn-link */
}

.sky-modal-full-page {
  width: 100%;
  top: 0;
  margin: 0;
}

.sky-modal-full-page .sky-modal-header-content {
  font-family: "Blackbaud Sans Condensed", "Helvetica Neue Condensed", "Arial Narrow";
  color: #282b31;
  font-weight: 300;
  font-size: 26px;
}

.sky-modal-full-page .sky-modal-header-buttons .fa-close {
  font-size: 20px;
}

.sky-modal-full-page .sky-modal-content {
  flex-grow: 1;
}

:host /deep/ .sky-sectioned-form {
  height: 460px;
  margin: -15px;
}

