styles:
.sky-popover-container {
  position: fixed;
  z-index: 999;
  min-width: 276px;
  max-width: 276px;
}

.sky-popover-container.sky-popover-hidden {
  visibility: hidden;
  opacity: 0;
  top: -9999px !important;
  left: -9999px !important;
}

.sky-popover-container:focus {
  outline: none;
}

@media (max-width: 276px) {
  .sky-popover-container {
    max-width: 100%;
    min-width: auto;
  }
}

.sky-popover {
  background-color: #ffffff;
}

.sky-popover-header {
  padding: 10px 10px 0 10px;
}

.sky-popover-header + .sky-popover-body {
  padding-top: 2px;
}

.sky-popover-title {
  font-family: "Blackbaud Sans", "Helvetica Neue", Arial, sans-serif;
  color: #282b31;
  font-weight: 600;
  font-size: 16px;
  margin: 0;
}

.sky-popover-body {
  padding: 10px;
}

.sky-popover-arrow {
  width: 0;
  height: 0;
  position: absolute;
  border: 10px solid transparent;
}

.sky-popover-placement-fullscreen {
  background-color: rgba(0, 0, 0, 0.2);
  padding: 10px !important;
  top: 0 !important;
  left: 0 !important;
  right: 0;
  bottom: 0;
  width: 100% !important;
  max-width: none;
  max-height: 100%;
  height: auto !important;
}

.sky-popover-placement-fullscreen > .sky-popover {
  border-top: 1px solid #cdcfd2;
  border-bottom: 1px solid #cdcfd2;
  border-left: 1px solid #cdcfd2;
  border-right: 1px solid #cdcfd2;
  overflow: auto;
  max-height: 100%;
  display: flex;
  flex-direction: column;
}

.sky-popover-placement-fullscreen > .sky-popover .sky-popover-header {
  padding: 10px;
  border-bottom: 1px solid #e2e3e4;
}

.sky-popover-placement-fullscreen > .sky-popover .sky-popover-body {
  position: relative;
}

.sky-popover-placement-above {
  padding-bottom: 10px;
}

.sky-popover-placement-above .sky-popover {
  border-bottom: 10px solid #00b4f1;
}

.sky-popover-placement-above .sky-popover-arrow {
  border-bottom: 0;
  border-top-color: #00b4f1;
  bottom: 0;
  left: 50%;
  margin-left: -10px;
}

.sky-popover-placement-below {
  padding-top: 10px;
}

.sky-popover-placement-below .sky-popover {
  border-top: 10px solid #00b4f1;
}

.sky-popover-placement-below .sky-popover-arrow {
  border-top: 0;
  border-bottom-color: #00b4f1;
  top: 0;
  left: 50%;
  margin-left: -10px;
}

.sky-popover-placement-right {
  padding-left: 10px;
}

.sky-popover-placement-right .sky-popover {
  border-left: 10px solid #00b4f1;
}

.sky-popover-placement-right .sky-popover-arrow {
  border-left: 0;
  border-right-color: #00b4f1;
  left: 0;
  top: 50%;
  margin-top: -10px;
}

.sky-popover-placement-left {
  padding-right: 10px;
}

.sky-popover-placement-left .sky-popover {
  border-right: 10px solid #00b4f1;
}

.sky-popover-placement-left .sky-popover-arrow {
  border-right: 0;
  border-left-color: #00b4f1;
  right: 0;
  top: 50%;
  margin-top: -10px;
}

.sky-popover-placement-above.sky-popover-alignment-left .sky-popover-arrow,
.sky-popover-placement-below.sky-popover-alignment-left .sky-popover-arrow {
  left: 40px;
  right: auto;
}

.sky-popover-placement-above.sky-popover-alignment-right .sky-popover-arrow,
.sky-popover-placement-below.sky-popover-alignment-right .sky-popover-arrow {
  left: auto;
  right: 40px;
}

