styles:
.sky-sort-item {
  background-color: transparent;
  border: none;
  display: block;
  margin: 4px;
  min-width: 160px;
  text-align: left;
  transition: background-color 150ms;
}

.sky-sort-item.sky-dropdown-item-active, .sky-sort-item:hover {
  background-color: #eeeeef;
}

.sky-sort-item.sky-dropdown-item-disabled {
  cursor: default;
}

.sky-sort-item.sky-dropdown-item-disabled:hover {
  background-color: transparent;
}

.sky-sort-item ::ng-deep > button {
  background-color: transparent;
  border: none;
  color: #282b31;
  cursor: pointer;
  display: block;
  padding: 3px 20px;
  text-align: left;
  width: 100%;
}

.sky-sort-item ::ng-deep > button[disabled] {
  color: #686c73;
}

.sky-sort-item ::ng-deep > button[disabled]:hover {
  cursor: default;
}

.sky-sort-item-selected {
  background-color: #f1eef6;
  padding: 4px;
  margin: 0;
}

