styles:
.sky-card {
  background-color: #fff;
  border-top: 1px solid #cdcfd2;
  border-bottom: 1px solid #cdcfd2;
  border-left: 1px solid #cdcfd2;
  border-right: 1px solid #cdcfd2;
  display: inline-flex;
  flex-direction: column;
  height: 350px;
  margin: 0 10px 10px 0;
  transition: background-color 150ms;
  vertical-align: top;
  width: 350px;
}

.sky-card-small {
  height: 250px;
  width: 225px;
}

.sky-card-selectable .sky-card-header,
.sky-card-selectable .sky-card-content {
  cursor: pointer;
}

.sky-card-selected {
  background-color: #f1eef6;
  transition: background-color 150ms;
}

.sky-card-header {
  align-items: center;
  display: flex;
  flex-shrink: 0;
  margin: 0;
  padding: 10px 0 0 0;
}

.sky-card-heading-left,
.sky-card-heading-right {
  font-weight: normal;
  max-width: 34px;
}

.sky-card-heading-left {
  padding-left: 10px;
}

.sky-card-heading-right {
  padding-right: 10px;
}

.sky-card-heading-middle {
  flex-grow: 1;
  padding: 0 10px;
  overflow: hidden;
}

.sky-card-title {
  font-family: "Blackbaud Sans Condensed", "Helvetica Neue Condensed", "Arial Narrow";
  color: #282b31;
  font-weight: 300;
  font-size: 26px;
  margin: 0;
  line-height: 1.42857;
}

.sky-card-check {
  flex-shrink: 0;
  padding-right: 10px;
}

.sky-card-check .sky-check-wrapper {
  margin-bottom: 2px;
}

.sky-card-content {
  flex-grow: 1;
  font-weight: normal;
  margin: 0;
  padding: 10px;
  overflow: hidden;
}

.sky-card-actions {
  bottom: 0;
  flex-shrink: 0;
  text-align: center;
}

.sky-card-actions .sky-context-menu-btn {
  height: 32px;
  width: 32px;
}

