styles:
.sky-tab-dropdown ::ng-deep .sky-dropdown-item {
  margin: 0;
}

.sky-tab-dropdown-button {
  max-width: 300px;
}

.sky-tab-dropdown-item {
  display: flex;
  padding: 0;
  transition: background-color 150ms;
  min-width: 300px;
}

.sky-tab-dropdown-item:hover:not(.sky-tab-dropdown-item-selected) {
  background-color: #eeeeef;
}

.sky-tab-dropdown-item-btn {
  border: none;
  background-color: transparent;
  cursor: pointer;
  flex-grow: 1;
  padding: 10px;
  text-align: left;
}

.sky-tab-dropdown-item-close {
  background-color: transparent;
  border: none;
  padding: 10px;
}

.sky-tab-dropdown-item-selected {
  background-color: #007ca6;
}

.sky-tab-dropdown-item-selected:hover {
  background-color: #007ca6;
}

.sky-tab-dropdown-item-selected .sky-tab-dropdown-item-btn,
.sky-tab-dropdown-item-selected .sky-tab-dropdown-item-close {
  color: #fff;
}

