styles:
:host {
  display: flex;
  padding: 0 10px;
}

.sky-tile-dashboard-layout-multi {
  display: block;
}

.sky-tile-dashboard-layout-single {
  display: none;
}

@media (max-width: 991px) {
  :host {
    display: block;
    padding: 0;
  }
  .sky-tile-dashboard-layout-multi {
    display: none;
  }
  .sky-tile-dashboard-layout-single {
    display: block;
  }
  :host /deep/ .sky-tile-dashboard-column {
    padding: 0;
  }
}

@media (max-width: 767px) {
  :host /deep/ .sky-tile {
    margin-bottom: 0 !important;
  }
}

