styles:
.sky-tile {
  background-color: #fff;
  margin-bottom: 20px;
}

.sky-tile-header {
  border-color: #cdcfd2;
  border-style: solid solid none;
  border-width: 4px 0 0;
  display: flex;
}

.sky-tile-header-content {
  display: flex;
  flex: 1;
  align-items: baseline;
  cursor: pointer;
}

.sky-tile-title {
  font-family: "Blackbaud Sans Condensed", "Helvetica Neue Condensed", "Arial Narrow";
  color: #282b31;
  font-weight: 300;
  font-size: 26px;
  margin: 0;
  padding: 10px 15px;
}

.sky-tile-summary {
  font-family: "Blackbaud Sans Condensed", "Helvetica Neue Condensed", "Arial Narrow";
  color: #282b31;
  font-weight: 500;
  font-size: 22px;
  color: #007ca6;
  opacity: 0;
  padding-right: 15px;
  transition: opacity 250ms;
  max-height: 30px;
  overflow: hidden;
}

.sky-tile-collapsed .sky-tile-summary {
  opacity: 1;
}

.sky-tile-header-column-tools {
  flex-shrink: 0;
}

.sky-tile-content {
  border-color: #e2e3e4;
  border-image: none;
  border-style: solid solid none;
  border-width: 1px 0;
}

:host /deep/ .sky-tile-tools .sky-chevron {
  margin: 12px 9px 12px 0;
}

.sky-tile-settings {
  color: #cdcfd2;
  cursor: pointer;
  height: 24px;
  margin: 12px 9px 12px 0;
  padding: 0;
  background-color: transparent;
  border: none;
  width: 24px;
}

.sky-tile-settings:hover {
  color: #979ba2;
  transition: color 150ms;
}

.sky-tile-grab-handle {
  color: #cdcfd2;
  cursor: pointer;
  cursor: move;
  cursor: -webkit-grab;
  cursor: -moz-grab;
  font-size: 15px;
  padding: 15px 15px 15px 0;
}

.sky-tile-grab-handle:hover {
  color: #979ba2;
  transition: color 150ms;
}

