styles:
.sky-input-group-timepicker-btn /deep/ .sky-btn {
  border-radius: 0;
}

.sky-input-group-timepicker-btn /deep/ .sky-dropdown-menu {
  box-shadow: none;
  background-color: transparent;
  text-align: center;
}

.sky-input-group /deep/ .sky-dropdown-item {
  box-shadow: 0 0 3px 0;
}

.sky-timepicker-container {
  font-size: 15px;
  display: flex;
  padding: 5px;
  background-color: #eeeeef;
}

.sky-timepicker-container button {
  background-color: #ffffff;
  border-width: 0;
  padding: 15px;
  padding-top: 5px;
  padding-bottom: 5px;
  width: 100%;
  height: 100%;
}

.sky-timepicker-container :last-child ol {
  display: flex;
  flex-direction: column;
  height: 100%;
}

.sky-timepicker-container :last-child ol li {
  border-bottom: 1px solid #e2e3e4;
  flex: 1;
}

.sky-timepicker-column {
  margin: 5px;
}

.sky-timepicker-column ol {
  border-top: 1px solid #e2e3e4;
  border-right: 1px solid #e2e3e4;
  border-bottom: 1px solid #e2e3e4;
  border-left: 1px solid #e2e3e4;
  column-gap: 1px;
  columns: 2;
  list-style-type: none;
  margin: 0;
  padding: 0;
}

.sky-timepicker-column ol li {
  text-align: center;
  cursor: pointer;
  margin: 0;
}

.sky-timepicker-column ol li button {
  cursor: pointer;
}

.sky-timepicker-column ol li button:focus {
  outline: thin dotted;
  outline: -webkit-focus-ring-color auto 5px;
  outline-offset: -2px;
}

.sky-timepicker-column ol li button:hover {
  background-color: #eeeeef;
}

.sky-timepicker-column ol li button:active {
  background-color: #e2e3e4;
}

.sky-timepicker-column ol li button.sky-btn-active:hover {
  background-color: #007ca6;
}

.sky-timepicker-column ol .sky-btn-active {
  background-color: #007ca6;
  color: #ffffff;
}

.sky-timepicker-column.sky-timepicker-24hour ol {
  columns: 4;
}

.sky-timepicker-column.sky-timepicker-24hour ol li {
  border-bottom-width: 0px;
}

.sky-timepicker-footer {
  margin: 0px;
  padding: 10px;
  padding-top: 0px;
}

.sky-timepicker-footer .sky-timepicker-column {
  width: 100%;
}

.sky-timepicker-footer button {
  border-style: solid;
  border-width: 1px;
  border-color: #e2e3e4;
  padding: 10px;
  cursor: pointer;
}

